mapper.delete:
update cart 
set deleted = 1, updated_time = now()
where user_id = #{userId,jdbcType=BIGINT} 
and product_id = #{productId,jdbcType=BIGINT}
and sku_id = #{skuId,jdbcType=BIGINT}

mapper.update:
update cart 
set quantity = #{quantity,jdbcType=INTEGER},
    updated_time = now()
where user_id = #{userId,jdbcType=BIGINT} 
and product_id = #{productId,jdbcType=BIGINT}
and sku_id = #{skuId,jdbcType=BIGINT}
and deleted = 0

update cart 
set selected = #{selected,jdbcType=TINYINT},
    updated_time = now()
where user_id = #{userId,jdbcType=BIGINT} 
and id = #{cartId,jdbcType=BIGINT}
and deleted = 0
